select a.id,a.longitude,a.latitude,a.qyname,a.department,a.address,a.scpermit,a.youxiaodate,a.enterpriseType,a.qydelegate,a.qyphone,a.zzcode,b.t_dictionary_name as addressName,a.create_time,a.scaddress,d1.t_dictionary_name departmentName,a.fazhengriqi,a.fddelegate from (select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_1' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from agricultural union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_8' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from cosmetic union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_3' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from criculate union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_9' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from drug union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_4' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from food union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_7' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from health union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_5' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from industry union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_10' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from medical union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_2' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from production union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate,'enterprise_11' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from usemed union all select id,longitude,latitude,department,qyname,address,scpermit,youxiaodate ,'enterprise_6' as enterpriseType,qydelegate,qyphone,zzcode,create_time,scaddress,fazhengriqi,fddelegate from wine) a left join t_dictionary b on a.address = b.t_dictionary_code left join t_dictionary d1 on d1.t_dictionary_code=a.department where 1=1
and a.qyname = #{enterpriseName,jdbcType=VARCHAR}
and a.qyname like CONCAT('%', #{enterpriseNameLike,jdbcType=VARCHAR},'%')
and a.enterpriseType = #{enterpriseType}
and a.department = #{checkUnitCode}
and a.address = #{address}
and a.fazhengriqi = #{fazhengriqi}
and a.department = #{department}
and a.longitude <> '' and a.latitude <> '' and a.longitude is not null and a.latitude is not null group by a.longitude,a.latitude
order by a.create_time desc
limit ${startNum},${limit}

