SELECT a.id,b.t_dictionary_name AS enterpriseTypeName,a.qyname,b.t_dictionary_code AS enterpriseType ,a.address,a.department,c.t_dictionary_name AS region, d.t_dictionary_name AS unitName FROM (SELECT id,qyname,address,department,'enterprise_1' AS enterpriseType FROM agricultural UNION ALL SELECT id,qyname,address,department,'enterprise_8' AS enterpriseType FROM cosmetic UNION ALL SELECT id,qyname,address,department,'enterprise_3' AS enterpriseType FROM criculate UNION ALL SELECT id,qyname,address,department,'enterprise_9' AS enterpriseType FROM drug UNION ALL SELECT id,qyname,address,department,'enterprise_4' AS enterpriseType FROM food UNION ALL SELECT id,qyname,address,department,'enterprise_7' AS enterpriseType FROM health UNION ALL SELECT id,qyname,address,department,'enterprise_5' AS enterpriseType FROM industry UNION ALL SELECT id,qyname,address,department,'enterprise_10' AS enterpriseType FROM medical UNION ALL SELECT id,qyname,address,department,'enterprise_2' AS enterpriseType FROM production UNION ALL SELECT id,qyname,address,department,'enterprise_11' AS enterpriseType FROM usemed UNION ALL SELECT id,qyname,address,department,'enterprise_6' AS enterpriseType FROM wine) a INNER JOIN t_dictionary b ON a.enterpriseType = b.t_dictionary_code INNER JOIN t_dictionary c ON a.address = c.t_dictionary_code INNER JOIN t_dictionary d ON a.department = d.t_dictionary_code WHERE 1=1
and a.enterpriseType = #{enterpriseType}
and a.qyname like CONCAT('%', #{enterpriseName,jdbcType=VARCHAR},'%')
and a.address = #{regionCode}
and a.department = #{unitCode}
limit ${startNum},${limit}

SELECT count(*) FROM (SELECT a.id,b.t_dictionary_name AS enterpriseTypeName,a.qyname,b.t_dictionary_code AS enterpriseType ,a.address,a.department,c.t_dictionary_name AS region, d.t_dictionary_name AS unitName FROM (SELECT id,qyname,address,department,'enterprise_1' AS enterpriseType FROM agricultural UNION ALL SELECT id,qyname,address,department,'enterprise_8' AS enterpriseType FROM cosmetic UNION ALL SELECT id,qyname,address,department,'enterprise_3' AS enterpriseType FROM criculate UNION ALL SELECT id,qyname,address,department,'enterprise_9' AS enterpriseType FROM drug UNION ALL SELECT id,qyname,address,department,'enterprise_4' AS enterpriseType FROM food UNION ALL SELECT id,qyname,address,department,'enterprise_7' AS enterpriseType FROM health UNION ALL SELECT id,qyname,address,department,'enterprise_5' AS enterpriseType FROM industry UNION ALL SELECT id,qyname,address,department,'enterprise_10' AS enterpriseType FROM medical UNION ALL SELECT id,qyname,address,department,'enterprise_2' AS enterpriseType FROM production UNION ALL SELECT id,qyname,address,department,'enterprise_11' AS enterpriseType FROM usemed UNION ALL SELECT id,qyname,address,department,'enterprise_6' AS enterpriseType FROM wine) a INNER JOIN t_dictionary b ON a.enterpriseType = b.t_dictionary_code INNER JOIN t_dictionary c ON a.address = c.t_dictionary_code INNER JOIN t_dictionary d ON a.department = d.t_dictionary_code) co WHERE 1=1
and co.enterpriseType = #{enterpriseType}
and co.qyname like CONCAT('%', #{enterpriseName,jdbcType=VARCHAR},'%')
and co.address = #{regionCode}
and co.department = #{unitCode}
