mapper.insert:
insert into t_sampling_task (enterpriseId, enterprise_type,checkStartDate,checkDate,
checkEndDate, unitCode, unitName,
batch, sampling_type
)
values

  (#{item.enterpriseId}, #{item.enterpriseType}, #{item.checkStartDate},#{item.checkDate},
  #{item.checkEndDate}, #{item.unitCode}, #{item.unitName},
  #{item.batch}, #{item.samplingType}
  )

mapper.select:
select * from (SELECT DISTINCT t.id, t.enterpriseId,a.qyname, CASE t.sampling_type WHEN '1' THEN '食品安全抽样单' WHEN '2' THEN '药品安全抽样检验单' WHEN '3' THEN '化妆品安全抽样单' WHEN '4' THEN '医疗器械安全抽样单' WHEN '5' THEN '保健食品安全抽样单' END AS sampTypeName, t.enterprise_type, d.t_dictionary_name AS enterpriseTypeName ,t.checkStartDate, t.checkEndDate,t.`checkDate`, t.batch, t.unitCode, t.unitName, t.sampling_type, IF(( SELECT COUNT(i.id) FROM t_sampling_inspection i WHERE t.id=i.task_id AND form_type=1)>=1 and ( SELECT COUNT(i.id) FROM t_sampling_inspection i WHERE t.id=i.task_id AND form_type=2)>=1,"已完成","未完成") AS state
FROM t_sampling_task t LEFT JOIN t_dictionary d ON t.enterprise_type = d.t_dictionary_code LEFT JOIN (SELECT id,qyname,address,department,'enterprise_1' AS enterpriseType FROM agricultural UNION ALL SELECT id,qyname,address,department,'enterprise_8' AS enterpriseType FROM cosmetic UNION ALL SELECT id,qyname,address,department,'enterprise_3' AS enterpriseType FROM criculate UNION ALL SELECT id,qyname,address,department,'enterprise_9' AS enterpriseType FROM drug UNION ALL SELECT id,qyname,address,department,'enterprise_4' AS enterpriseType FROM food UNION ALL SELECT id,qyname,address,department,'enterprise_7' AS enterpriseType FROM health UNION ALL SELECT id,qyname,address,department,'enterprise_5' AS enterpriseType FROM industry UNION ALL SELECT id,qyname,address,department,'enterprise_10' AS enterpriseType FROM medical UNION ALL SELECT id,qyname,address,department,'enterprise_2' AS enterpriseType FROM production UNION ALL SELECT id,qyname,address,department,'enterprise_11' AS enterpriseType FROM usemed UNION ALL SELECT id,qyname,address,department,'enterprise_6' AS enterpriseType FROM wine) a ON t.enterpriseId=a.id AND t.`enterprise_type`=a.enterpriseType LEFT JOIN t_sampling_inspection i ON t.id=i.task_id WHERE 1=1
and a.enterpriseType = #{enterpriseType}
and t.unitCode = #{unitCode}
and a.qyname like CONCAT('%', #{enterpriseName,jdbcType=VARCHAR},'%')
and t.checkStartDate >= #{startCalenderOne}
and t.checkEndDate <= #{endCalenderOne}
order by t.id desc) w order by w.state desc
limit ${startNum},${limit}

SELECT count(DISTINCT t.id) FROM t_sampling_task t LEFT JOIN t_dictionary d ON t.enterprise_type = d.t_dictionary_code LEFT JOIN (SELECT id,qyname,address,department,'enterprise_1' AS enterpriseType FROM agricultural UNION ALL SELECT id,qyname,address,department,'enterprise_8' AS enterpriseType FROM cosmetic UNION ALL SELECT id,qyname,address,department,'enterprise_3' AS enterpriseType FROM criculate UNION ALL SELECT id,qyname,address,department,'enterprise_9' AS enterpriseType FROM drug UNION ALL SELECT id,qyname,address,department,'enterprise_4' AS enterpriseType FROM food UNION ALL SELECT id,qyname,address,department,'enterprise_7' AS enterpriseType FROM health UNION ALL SELECT id,qyname,address,department,'enterprise_5' AS enterpriseType FROM industry UNION ALL SELECT id,qyname,address,department,'enterprise_10' AS enterpriseType FROM medical UNION ALL SELECT id,qyname,address,department,'enterprise_2' AS enterpriseType FROM production UNION ALL SELECT id,qyname,address,department,'enterprise_11' AS enterpriseType FROM usemed UNION ALL SELECT id,qyname,address,department,'enterprise_6' AS enterpriseType FROM wine) a ON t.enterpriseId=a.id AND t.`enterprise_type`=a.enterpriseType WHERE 1=1
and a.enterpriseType = #{enterpriseType}
and t.unitCode = #{unitCode}
and a.qyname like CONCAT('%', #{enterpriseName,jdbcType=VARCHAR},'%')
and t.checkStartDate >= #{startCalenderOne}
and t.checkEndDate <= #{endCalenderOne}
