mapper.select:
select kr.*,af.purchase_time from t_keep_record kr left join t_additive_filing af on af.id=kr.additiveFiling_id where kr.additiveFiling_id = #{additivefilingId,jdbcType=INTEGER}
and kr.additives_name LIKE CONCAT('%', #{additivesName} , '%')
and af.purchase_time LIKE CONCAT('%', #{purchaseTime} , '%')

select count(*) from t_keep_record kr left join t_additive_filing af on af.id=kr.additiveFiling_id where kr.additiveFiling_id = #{additivefilingId,jdbcType=INTEGER}
and kr.additives_name LIKE CONCAT('%', #{additivesName} , '%')
and af.purchase_time LIKE CONCAT('%', #{purchaseTime} , '%')
